CalendarTest = (function () {
	"use strict";
	return function () {
		var _tests = {};

		return {
			addTest: function (key, array) {
				_tests[key] = array;
			},
			getTest: function (key) {
				return _tests[key];
			},
			renderTextCalendar: function (key) {
				var calItems = this.getTest(key);

				var padLeft = function (number, padLength, padChar) {
					var stringRep = number + "";
					while (stringRep.length < padLength) {
						stringRep = padChar + stringRep;
					}
					return stringRep;
				};

				var output = [];
				output.push(
					_.map(calItems, function (item, i) {
						if (0 === (i % 7)) {
							output.push("\n");
						}

						if (item === "") {
							output.push("   ");
						}
						else {
							output.push(padLeft(item, 3, " "));
						}
					})
				);
				output.push("\n");

				return _.flatten(output).join('');
			}
		};
	};
} ());

